import React, {useState, useEffect} from 'react';
import './css/App.css';
//import style from './css/form.module.css';
import style from './css/genView.module.css';
import {Link} from 'react-router-dom'; 
import { useField } from '@formiz/core';
import { createStore, combineReducers } from 'redux';
import { reducer as formReducer } from 'redux-form';


function Shop() {

  const MyField = (props) => {
    const { errorMessage, isValid, setValue, value } = useField(props)

  }
  return (
    <React.Fragment>
    <header className={style.header_views}>     
      <img src={require("./images/background_dos.jpg")}  alt="fog over the bay"/>
      <div class={style.hero_text_net}>
     <h1>Cloud Architecture</h1>
  </div>
</header>

<body className={style.BodyView}>

  <section>

    <article className={style.article_view}>
    <input
        value={value ?? ''}
        onChange={e => setValue(e.target.value)}
      />
      {
        !isValid
        && errorMessage // Display error message
      }
    </article>


        <button className={style.consultButton}>Finish Scheduling</button>

  </section>

</body>
</React.Fragment>
  );
}

export default Shop;
