declare type Value = any;
/**
 * Check if the value exists.
 * @returns {function} function that accept a value and
 * return true if the value exists, false instead.
 */
export declare const isRequired: () => (value: Value) => boolean;
/**
 * Check if the value is a string and is not empty.
 */
export declare const isNotEmptyString: () => (value: Value) => boolean;
/**
 * Check if the value is an array and is not empty.
 */
export declare const isNotEmptyArray: () => (value: Value) => boolean;
/**
 * Check if the value is a valid email.
 */
export declare const isEmail: () => (value: Value) => boolean;
/**
 * Check if the value is a number.
 */
export declare const isNumber: () => (value: Value) => boolean;
/**
 * Check if the value is greater than the given minimum.
 */
export declare const isMinNumber: (min: number) => (value: Value) => boolean;
/**
 * Check if the value is lower than the given maximum.
 */
export declare const isMaxNumber: (max: number) => (value: Value) => boolean;
/**
 * Check if the value is between the minimum and the maximum.
 */
export declare const isInRangeNumber: (min: number, max: number) => (value: Value) => boolean;
/**
 * Check if the value is a percentage, between 0 and 100.
 */
export declare const isPercentage: () => (value: Value) => boolean;
/**
 * Check if the value has the given length.
 */
export declare const isLength: (length: number) => (value: Value) => boolean;
/**
 * Check if the value has a length greater than the given minimum.
 */
export declare const isMinLength: (min: number) => (value: Value) => boolean;
/**
 * Check if the value has a length lower than the given maximum.
 */
export declare const isMaxLength: (max: number) => (value: Value) => boolean;
export {};
